		// primitive data type: int, double, char, boolean.
		
		//1. int:
		
		int i = 20;
		i = 30; // This i = 30 will replace the value of i = 20. So, the latest value of i is 30.
		int j = 34;
		System.out.println(j);
		int k = 5;
		
		int p = 0;
		System.out.println(p);
		int q = -4;
		System.out.println( k+q );
		// int = 12.33, We can't assign int with decimal value
		
		
		//2. double:
		
		double d1 = 12.22;
		double d2 = 32.53;
		System.out.println(d2);
		double d3 = 22; // 22.00
				
		//3. char: only single digit value and should be written within single quote.
		
		char c = 'a';
		char c1 = 'A';
		char c2 = '1';
		char c3 = '$';
		System.out.println(c3);
		
		//4. boolean:
		
		boolean b1 = true; // These[true and false] are values but also these are keyword in java.
		boolean b2 = false;
		
		//5. String: String is a class, not a data type. It should be written within double quote.
		
		String s = "Hello World!";
		String s1 = "Selenium";
		String s2 = "Hi, this is my java code";
		System.out.println(s2);
		String s3 = "1000";
		String s4 = "12.33";
		
		
		
		
